package com.example.placebook.adapterimport android.app.Activityimport android.view.Viewimport com.example.placebook.databinding.ContentBookmarkInfoBindingimport com.example.placebook.ui.MapsActivityimport com.example.placebook.viewmodel.MapsViewModelimport com.google.android.gms.maps.GoogleMapimport com.google.android.gms.maps.model.Markerclass BookmarkInfoWindowAdapter(val context: Activity) : GoogleMap.InfoWindowAdapter {    private val binding =        ContentBookmarkInfoBinding.inflate(context.layoutInflater)    override fun getInfoWindow(marker: Marker): View? {        return null    }    override fun getInfoContents(marker: Marker): View? {        val imageView = binding.photo        when (marker.tag) {            is MapsActivity.PlaceInfo -> {                imageView.setImageBitmap((marker.tag as MapsActivity.PlaceInfo).image)            }            is MapsViewModel.BookmarkMarkerView -> {                val bookMarkview = marker.tag as MapsViewModel.BookmarkMarkerView                imageView.setImageBitmap(bookMarkview.getImage(context))            }        }        binding.title.text = marker.title ?: ""        binding.phone.text = marker.snippet ?: ""        return binding.root    }}